import { ajax } from '@devexpress/analytics-core/analytics-internal';
import { HandlerUri } from '../utils/settings';
export class FormatStringService {
    static saveCustomPattern(typeString, format) {
        return ajax(HandlerUri(), 'formatString', encodeURIComponent(JSON.stringify({ action: 'save', typeString: typeString, customFormatString: format })));
    }
    static removeCustomPattern(typeString, format) {
        return ajax(HandlerUri(), 'formatString', encodeURIComponent(JSON.stringify({ action: 'remove', typeString: typeString, customFormatString: format })));
    }
    static updatePreview(value, typeString, format) {
        return ajax(HandlerUri(), 'formatStringPreview', encodeURIComponent(JSON.stringify({ value: value, typeString: typeString, formatString: format })));
    }
}
FormatStringService.actions = { updatePreview: FormatStringService.updatePreview, removeCustomPattern: FormatStringService.removeCustomPattern, saveCustomPattern: FormatStringService.saveCustomPattern };
